    </div>
    <script th:inline="javascript">
        var cartIcon = document.querySelector(".fa-shopping-cart");
        var cartList = document.querySelector(".cart-list");

        cartIcon.addEventListener("click", function() {
            var isCartListVisible = cartList.style.display === "block";
            cartList.style.display = isCartListVisible ? "none" : "block";
        });

        /*=============================================================================*/
        const menu = document.getElementById("overlay");

        function toggleMenu() {
            menu.classList.toggle("show1")
        }
        window.onclick = (event) => {
            if (!event.target.matches('.pucharse-button')) {
                if (menu.classList.contains("show1")) {
                    menu.classList.remove("show1")
                }
            }
        }
        const menu2 = document.getElementById("overlay-panel");
        menu2.addEventListener('click', event => event.stopPropagation())
        /*=============================================================================*/
        $("select").on("click", function() {

            $(this).parent(".select-box").toggleClass("open");

        });

        $(document).mouseup(function(e) {
            var container = $(".select-box");

            if (container.has(e.target).length === 0) {
                container.removeClass("open");
            }
        });


        $("select").on("change", function() {

            var selection = $(this).find("option:selected").text(),
                labelFor = $(this).attr("id"),
                label = $("[for='" + labelFor + "']");

            label.find(".label-desc").html(selection);

        });
        /*==============================================================================*/




        $('#card-num-validation').on('input', function(evt) {
            var value = evt.target.value
            $('span#error').remove();
            if (value.length === 0) {
                $('#card-num-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>')
                evt.target.className = 'invalid-blank'
                return
            }
            if ($.isNumeric(value)) {
                evt.target.className = 'valid'
            } else {
                $('#card-num-validation').after('<span class="error-message" id="error">Please enter numbers only!</span>')
                evt.target.className = 'invalid'
            }
        })

        $('#cvc-validation').on('input', function(evt) {
            var value = evt.target.value
            $('span#error').remove();
            if (value.length === 0) {
                $('#cvc-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>')
                evt.target.className = 'invalid-blank'
                return
            }
            if ($.isNumeric(value)) {
                evt.target.className = 'valid'
            } else {
                $('#cvc-validation').after('<span class="error-message" id="error">Please enter numbers only!</span>')
                evt.target.className = 'invalid'
            }
        })

        $('#date-validation').on('input', function(evt) {
            var value = evt.target.value;
            $('span#error').remove();
            if (value.length === 0) {
                $('#date-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                evt.target.className = 'invalid-blank';

            } else {
                evt.target.className = 'valid';
            }
        })
        $('#card-name-validation').on('input', function(evt) {
            var value = evt.target.value;
            $('span#error').remove();
            if (value.length === 0) {
                $('#card-name-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                evt.target.className = 'invalid-blank';

            } else {
                evt.target.className = 'valid';
            }
        })

        document.getElementById('confirm-payment-btn').addEventListener('click', function(event) {
            if ($('#cvc-validation').val().length === 0) {
                $('#cvc-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                $('#cvc-validation').addClass('invalid-blank');
                event.preventDefault();
            }
            if ($('#card-num-validation').val().length === 0) {
                $('#card-num-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                $('#card-num-validation').addClass('invalid-blank');
                event.preventDefault();
            }
            if ($('#date-validation').val().length === 0) {
                $('#date-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                $('#date-validation').addClass('invalid-blank');
                event.preventDefault();
            }
            if ($('#card-name-validation').val().length === 0) {
                $('#card-name-validation').after('<span class="blank-message" id="error">Dont leave it blank!</span>');
                $('#card-name-validation').addClass('invalid-blank');
                event.preventDefault();
            }
            if ($('#cvc-validation').hasClass('invalid') || $('#cvc-validation').hasClass('invalid-blank') || $('#card-num-validation').hasClass('invalid') ||
                $('#card-num-validation').hasClass('invalid-blank') || $('#date-validation').hasClass('invalid-blank') || $('#card-name-validation').hasClass('invalid-blank')) {
                event.preventDefault();
            }
        });
    </script>
</body>

</html>